import React, { PropsWithChildren, RefObject, useContext } from "react";
import {
  Anchor,
  Box,
  Burger,
  Flex,
  Indicator,
  NavLink,
  Text,
  ThemeIcon,
} from "@mantine/core";
import styles from "./sidebar-item.module.scss";
import { useCallback } from "react";
import { useActivePath } from "@hooks/useActivePath";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { AppLayoutContext } from "../layouts/main-layout";
import { useNetwork } from "@mantine/hooks";
import Image from "next/image";
import { IconPoint } from "@tabler/icons-react";

export interface SidebarLinks {
  label: string;
  icon?: any;
  link?: string;
  links?: SidebarLinks[];
  divider?: boolean;
  external?: boolean;
  items?: SidebarLinks[];
}

export interface SidebarItemsProps {
  items: SidebarLinks[];
}

const Sidebar = ({ children }: PropsWithChildren) => {
  return <div className="w-full h-full flex flex-col">{children}</div>;
};
const SidebarHeader: React.FC = () => {
  const { toggleSidebar, sideBarOpened } = useContext(AppLayoutContext);

  return (
    <div className="p-4 flex justify-center items-center">
      <Flex flex={1} className="justify-center items-center">
        <Box bg="white" className=" rounded-full size-25">
          <Box className="relative size-20">
            <Image
              alt="logo"
              width={120}
              height={120}
              src="data:image/png;base64,"
            />
          </Box>
        </Box>
      </Flex>
      <Burger
        opened={sideBarOpened}
        onClick={toggleSidebar}
        hiddenFrom="sm"
        size="sm"
      />
    </div>
  );
};
interface SidebarItemProps extends SidebarLinks {
  ref?:
    | ((instance: HTMLButtonElement | null) => void)
    | RefObject<HTMLButtonElement>
    | null
    | undefined;
  type?: "child" | "root";
  children?: React.ReactNode;
}
const SidebarItem: React.FC<SidebarItemProps> = (props) => {
  const { toggleSidebar } = useContext(AppLayoutContext);
  const router = useRouter();
  const { link, type, label, icon, children } = props;
  const handleClick = (link: string) => {
    toggleSidebar && toggleSidebar();
    router.push(link);
  };

  const isActive = useActivePath();
  const selectedItemRef = useCallback(
    (node: HTMLAnchorElement, selected: boolean) => {
      if (node && selected) {
        node.scrollIntoView({ behavior: "smooth" });
      }
    },
    []
  );
  const defaultProps = {
    active: isActive(link),
    classNames: styles,
    label: (
      <Text
        fz={type === "child" ? 14.8 : 14.8}
        fw={type === "child" ? 400 : 400}
        maw={150}
        c="inherit"
        truncate
      >
        {label}
      </Text>
    ),
    leftSection: icon ? (
      <ThemeIcon
        variant="filled"
        autoContrast
        aria-label={label}
        color="blue.2"
      >
        {icon}
      </ThemeIcon>
    ) : (
      <IconPoint />
    ),
    className: `rounded-md text-white hover:text-black hover:bg-white ${
      type === "child" ? "border-l border-white" : ""
    }`,
    ref: (node: HTMLAnchorElement) => selectedItemRef(node, isActive(link)),
    fw: 500,
    children,
    my: 3,
    px: type === "child" ? 8 : 8,
  };
  return (
    <>
      {link ? (
        <NavLink
          component={Link}
          href={link}
          onClick={() => link && handleClick(link)}
          {...defaultProps}
        />
      ) : (
        <NavLink {...defaultProps} />
      )}
    </>
  );
};

const SidebarItems: React.FC<SidebarItemsProps> = ({ items }) => {
  function createNavLinks(links: SidebarLinks[], type?: "child" | "root") {
    return links.map((link, index) => (
      <SidebarItem key={link.label + String(index)} {...link} type={type}>
        {link.items && createNavLinks(link.items, "child")}
      </SidebarItem>
    ));
  }
  return <div className="flex-1 px-4"> {createNavLinks(items, "root")}</div>;
};
const SidebarFooter: React.FC = () => {
  const networkStatus = useNetwork();
  const currentYear = new Date().getFullYear();

  return (
    <div className="py-4 flex justify-between items-center border-t mx-4">
      <div className=" text-xs ">
        <div className="flex justify-between items-center">
          <Indicator
            processing
            color={networkStatus.online ? "teal" : "red"}
            size={9}
            position="middle-end"
          >
            <Text
              fw={500}
              color={networkStatus.online ? "teal" : "red"}
              size="sm"
              pr={10}
            >
              {networkStatus.online ? "Online" : "Offline"}
            </Text>
          </Indicator>
          <div className="text-center"></div>{" "}
        </div>
        <div className="flex gap-2 justify-between items-center">
          <div> &copy; {currentYear} </div>
          <div className="flex gap-2 justify-between items-center"></div>
        </div>
      </div>
    </div>
  );
};

Sidebar.Header = SidebarHeader;
Sidebar.Footer = SidebarFooter;
Sidebar.Items = SidebarItems;
Sidebar.Item = SidebarItem;

export { Sidebar };
